package com.samsung.android.app;

import android.content.Context;
import android.content.pm.PackageInfo;
import android.content.pm.PackageManager.NameNotFoundException;
import android.content.pm.Signature;
import android.os.Debug;
import android.util.SparseArray;
import android.util.secutil.Log;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.Iterator;

public class WhiteListForCategoryOrder {
    private static final boolean DEBUG = (Debug.isProductShip() == 0);
    private static final int SIGNATURE_MAX = 3;
    private static final int SIGNATURE_SAMSUNG_APPLICATION = 2;
    private static final int SIGNATURE_SAMSUNG_PLATFORM = 1;
    private static final int SIGNATURE_SAMSUNG_SHARED = 4;
    private static final String TAG = "WhiteListForCategoryOrder";
    private static WhiteListForCategoryOrder sInstance = null;
    private SparseArray<Signature> mSignaturesMap = new SparseArray();
    private HashMap<String, Integer> mWhiteList = new HashMap();

    public static WhiteListForCategoryOrder getInstance() {
        if (sInstance == null) {
            sInstance = new WhiteListForCategoryOrder();
        }
        return sInstance;
    }

    private void WhiteListForCategoryOrder() {
        setupSignaturesMap();
        setupWhiteList();
    }

    private void setupSignaturesMap() {
        this.mSignaturesMap.put(1, new Signature(""));
        this.mSignaturesMap.put(2, new Signature(""));
        this.mSignaturesMap.put(4, new Signature(""));
    }

    private void setupWhiteList() {
    }

    public boolean isAllowedToUseOrder(Context context, String packageName) {
        boolean signatureMatch;
        try {
            PackageInfo pkgInfo = context.getPackageManager().getPackageInfo(packageName, 64);
            if (pkgInfo == null) {
                if (!DEBUG) {
                    return false;
                }
                Log.d(TAG, "isAllowedToUse : pkgInfo is null");
                return false;
            } else if ((pkgInfo.applicationInfo.flags & 129) != 0) {
                return true;
            } else {
                Signature[] signatures = pkgInfo.signatures;
                if (hasSamsungKey(signatures)) {
                    return true;
                }
                Integer type = (Integer) this.mWhiteList.get(packageName);
                if (type != null) {
                    signatureMatch = isMatchedSignature(signatures, type.intValue());
                    if (!signatureMatch && DEBUG) {
                        Log.d(TAG, "isAllowedToUse : executable manager white lists don't match this App");
                    }
                    return signatureMatch;
                } else if (!DEBUG) {
                    return false;
                } else {
                    Log.d(TAG, "isAllowedToUse : executable manager white lists does not include this App");
                    return false;
                }
            }
        } catch (NameNotFoundException e) {
            if (DEBUG) {
                Log.d(TAG, "isNotAllowedToUse : " + e);
            }
            signatureMatch = false;
        }
    }

    private boolean hasSamsungKey(Signature[] signatures) {
        return isMatchedSignature(signatures, 7);
    }

    private ArrayList<Signature> getSignatures(int type) {
        ArrayList<Signature> list = new ArrayList();
        for (int i = 0; i < 3; i++) {
            int flag = 1 << i;
            if ((type & flag) != 0) {
                list.add(this.mSignaturesMap.get(flag));
            }
        }
        return list;
    }

    private boolean isMatchedSignature(Signature[] signatures, int type) {
        ArrayList<Signature> SIGNATURES = getSignatures(type);
        if (!(signatures == null || SIGNATURES == null)) {
            for (Signature signature : signatures) {
                Iterator i$ = SIGNATURES.iterator();
                while (i$.hasNext()) {
                    Signature s = (Signature) i$.next();
                    if (s != null && s.equals(signature)) {
                        return true;
                    }
                }
            }
        }
        return false;
    }
}
